sp_widget.client_script:
api.controller=function($rootScope, $scope) {
  /* widget controller */
  var c = this;
	c.selectItem = function(idx)
		{
		var id = c.data.notes[idx].sys_id;
		console.log('Notes List Client: selectNote emit noteID=' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
		}

	c.onEditNote = function(event, data)
		{
		var i;
		//find note object in the array
		console.log('Notes List Client: noteEdited caught, noteID=' + $rootScope.noteID);
		for(i = 0; i < c.data.notes.length; i++)
			if(c.data.notes[i].sys_id == $rootScope.noteID)
				break;
		if(i >= c.data.notes.length)
			return;	//bail out on non-matching note sys_id
		c.data.notes[i].title = $rootScope.noteTitle;
		c.data.notes[i].note = $rootScope.noteBody;
		console.log('Notes List Client: noteEdited caught, note[' + i + '] was updated.');
		}
	$rootScope.$on('noteEdited', c.onEditNote);
};

sp_widget.script:
(function() {
	//create an array to populate with notes GR
	data.notes = [];
	var grNote = new GlideRecord('x_snc_createnotes_note');
	grNote.addQuery('user', gs.getUser().getID());
	grNote.orderByDesc('sys_created_on');
	console.log('Notes List Server:' + grNote.getEncodedQuery());
	grNote.query();
	while(grNote.next())
		{
		var objNote = {};
		$sp.getRecordDisplayValues(objNote, grNote, 'number,title,sys_id');
		objNote.note = grNote.getValue('note').slice(0,20);
		data.notes.push(objNote);	
		}
})();
